    public void init() {
        for (int i = 0; i < arabicDua.length - 1; ++i) {
            duaItemArrayList.add(new DuaItem(arabicDua[i], transcriptDua[i],
                    translateDua[i], ""));
        }
    }

    public void initDrawer() {
        for(String i : drawerContentList){
            duaDrawer.add(new DrawerDuaContent(i));
        }
    }
